import java.util.*;
public class Practice6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int j = 1; j <= 5; j++)
		{	
			for(int i = 2; i <= 5; i++)
			{
				System.out.print(i + " * " + j + " = "+ (i * j) + "	");
			}
			System.out.println();
		}
		System.out.println();
		for(int j = 1; j <= 5; j++)
		{	
			for(int i = 6; i <= 9; i++)
			{
				System.out.print(i + " * " + j + " = "+ (i * j) + "	 ");
			}
			System.out.println();
		}
	}

}
